import React from 'react';
import BioCard from './BioCard.js'

const bios = [
  {
    name: 'Roger',
    text: 'The Horse Man',
    photo: ''
  },
  {
    name: 'Howard',
    text: 'The Cow Guy and the Hay Guy',
    photo: ''
  },
  {
    name: 'Elaine',
    text: "Nanny's Mom",
    photo: ''
  }
]

class Bios extends React.Component {
  constructor() {
    super()
    this.state = {
      bios
    }
  }

  // Need to connect to the Bios api like this?
